mapper.select:

	SELECT * FROM (
	     SELECT ROWNUM AS RNUM, A.* FROM (
	        		SELECT
	                    *
	                FROM
	                    mvc_board 
	                order by bGroup desc, bStep asc   
	    ) A WHERE ROWNUM <= #{pageNum} * #{amount}
	) WHERE RNUM > (#{pageNum}-1) * #{amount}
